import { useRef } from 'react';

import Input from '../../UI/Input';
import classes from './ChickensForm.module.css';
const ChickensForm = props => {
    const amountInputRef = useRef();
    
    const submitHandler = event =>{
        event.preventDefault(); 
    };
    return (
        <form className={classes.form} onSubmit={submitHandler}>
            <Input
                ref={useRef}
                lable="Amount"
                input={{
                    id: 'amount',
                    type: 'number',
                    min: '1',
                    max: '5',
                    step: '1',
                    defaultValue: '1'
                }} />
            <button >+ Add</button>
        </form>
    );
};

export default ChickensForm;
